const appliedCouponCodes=[];async function applyCoupon(code){const data=new FormData;data.append("security",php_data.apply_coupon_nonce);data.append("coupon_code",code);const response=await fetch("/?wc-ajax=apply_coupon",{method:"POST",body:data});const message=await response.text();if(message.includes("woocommerce-error")){window.alert(parseWooCommerceHTMLError(message))}updateCart();return response.status===200&&!message.includes("woocommerce-error")}async function fetchAndSendCoupon(event){const response=await fetch(`${baseStoreURL()}/wp-json/peachpay/v1/coupon/${event.data.code}`);const coupon=await response.json();if(!response.ok){alert(coupon.message);sendStopCouponLoadingMessage();return}if(php_data.should_place_order_before_payment){if(!await applyCoupon(coupon.code)){sendStopCouponLoadingMessage();return}appliedCouponCodes.push(coupon.code)}document.querySelector("#peachpay-iframe").contentWindow.postMessage({event:"coupon",coupon:coupon},"*")}function sendStopCouponLoadingMessage(){document.querySelector("#peachpay-iframe").contentWindow.postMessage({event:"stopCouponLoading"},"*")}function sendClearCouponLinesMessage(){document.querySelector("#peachpay-iframe").contentWindow.postMessage({event:"clearCouponLines"},"*")}